var serverPorts = function(){
	function getWebsocketPort(){
		var wsPort = 2883;
		if(process.env.WSOCKET_PORT == undefined){
			console.log('Env variable WSOCKET_PORT undefined, default value used');
		}else{
			wsPort = process.env.WSOCKET_PORT;
		}
		return wsPort;
	}

	function getWebServerPort(){
		var serverPort = 80;
		if(process.env.SERVER_PORT == undefined){
			console.log('Env variable SERVER_PORT undefined, default value used');
		}else{
			serverPort = process.env.SERVER_PORT;
		}
		return serverPort;
	}
	
	//Select which function to make public
	return {
		getWebsocketPort: getWebsocketPort,
		getWebServerPort: getWebServerPort
	}
}();

module.exports=serverPorts;

